"""Node builders for Claude Code translation to DiPeO diagrams."""

from typing import Any, Optional

from .diff_utils import DiffGenerator
from .text_utils import TextProcessor


class NodeBuilder:
    """Builds different types of nodes for DiPeO diagrams from Claude Code events."""

    def __init__(self):
        """Initialize the node builder."""
        self.node_counter = 0
        self.persons = {}
        self.text_processor = TextProcessor()
        self.diff_generator = DiffGenerator()

    def reset(self):
        """Reset the node builder state."""
        self.node_counter = 0
        self.persons = {}
        self.text_processor = TextProcessor()

    def increment_counter(self) -> int:
        """Increment and return the node counter."""
        self.node_counter += 1
        return self.node_counter

    def get_position(self) -> dict[str, int]:
        """Calculate node position based on current counter."""
        return {
            "x": 300 + (self.node_counter * 50) % 800,
            "y": 100 + (self.node_counter // 10) * 150,
        }

    def create_start_node(self, session_id: str, initial_prompt: str) -> dict[str, Any]:
        """Create the start node for the diagram."""
        return {
            "label": "Start",
            "type": "start",
            "position": {"x": 100, "y": 100},
            "props": {
                "trigger_mode": "manual",
                "custom_data": {
                    "session_id": session_id,
                    "initial_prompt": initial_prompt[:200]
                    if initial_prompt
                    else "Claude Code Session",
                },
            },
        }

    def create_user_node(self, content: str) -> dict[str, Any] | None:
        """Create a node for user input, or None if no meaningful input."""
        # Skip creating node if content is empty or just whitespace
        if not content or not content.strip():
            return None

        label = f"User Input {self.increment_counter()}"

        # Register user person if not exists
        if "user" not in self.persons:
            self.persons["user"] = {
                "service": "openai",
                "model": "user",
                "api_key_id": "USER_INPUT",
            }

        return {
            "label": label,
            "type": "person_job",
            "position": self.get_position(),
            "props": {
                "person": "user",
                "default_prompt": content,
                "max_iteration": 1,
            },
        }

    def create_assistant_node(self, content: str, system_messages: list[str] = None) -> dict[str, Any]:
        """Create a node for AI assistant response."""
        label = f"Claude Response {self.increment_counter()}"

        # Register Claude person if not exists
        if "claude_code" not in self.persons:
            # Build system prompt with meta/system messages if provided
            base_prompt = "You are Claude Code, an AI assistant helping with software development."
            if system_messages:
                # Add meta/system messages to provide context
                system_context = "\n\nAdditional context:\n" + "\n".join(system_messages[:5])  # Limit to first 5
                full_system_prompt = base_prompt + system_context
            else:
                full_system_prompt = base_prompt

            self.persons["claude_code"] = {
                "service": "anthropic",
                "model": "claude-code",
                "api_key_id": "APIKEY_CLAUDE",
                "system_prompt": full_system_prompt,
            }

        return {
            "label": label,
            "type": "person_job",
            "position": self.get_position(),
            "props": {
                "person": "claude_code",
                "default_prompt": f"Process and respond: {content[:200]}",
                "max_iteration": 1,
            },
        }

    def create_read_node(self, tool_input: dict[str, Any]) -> dict[str, Any]:
        """Create a DB node for file read operation."""
        label = f"Read File {self.increment_counter()}"
        file_path = tool_input.get("file_path", "unknown")

        return {
            "label": label,
            "type": "db",
            "position": self.get_position(),
            "props": {"operation": "read", "sub_type": "file", "file": file_path},
        }

    def create_write_node(self, tool_input: dict[str, Any]) -> dict[str, Any]:
        """Create a DB node for file write operation."""
        label = f"Write File {self.increment_counter()}"
        file_path = tool_input.get("file_path", "unknown")
        content = tool_input.get("content", "")

        return {
            "label": label,
            "type": "db",
            "position": self.get_position(),
            "props": {
                "operation": "write",
                "sub_type": "file",
                "file": file_path,
                "content": content[:1000],  # Store some content for context
            },
        }

    def create_edit_node(self, tool_name: str, tool_input: dict[str, Any], original_content: Optional[str] = None) -> dict[str, Any]:
        """Create a diff_patch node for file edit operation."""
        label = f"{tool_name} File {self.increment_counter()}"
        file_path = tool_input.get("file_path", "unknown")

        # Generate unified diff from old_string and new_string
        if tool_name == "Edit":
            # Unescape strings that may have been escaped in the Claude Code session
            old_string = self.text_processor.unescape_string(tool_input.get("old_string", ""))
            new_string = self.text_processor.unescape_string(tool_input.get("new_string", ""))

            # If we have original content, use it for better diff generation
            if original_content:
                modified_content = original_content.replace(old_string, new_string, 1)
                diff_content = self.diff_generator.generate_unified_diff(
                    file_path, original_content, modified_content
                )
            else:
                diff_content = self.diff_generator.generate_unified_diff(
                    file_path, old_string, new_string
                )
        elif tool_name == "MultiEdit":
            # For MultiEdit, combine all edits into a single diff
            edits = tool_input.get("edits", [])
            # Unescape strings in each edit
            for edit in edits:
                if "old_string" in edit:
                    edit["old_string"] = self.text_processor.unescape_string(edit["old_string"])
                if "new_string" in edit:
                    edit["new_string"] = self.text_processor.unescape_string(edit["new_string"])
            diff_content = self.diff_generator.generate_multiedit_diff(file_path, edits, original_content)
        else:
            # Fallback for unknown edit types
            diff_content = "# Unable to generate diff"

        return {
            "label": label,
            "type": "diff_patch",
            "position": self.get_position(),
            "props": {
                "target_path": file_path,
                "diff": diff_content,
                "format": "unified",
                "backup": True,
                "validate": True,
            },
        }

    def create_edit_node_with_result(
        self, tool_name: str, tool_input: dict[str, Any], tool_use_result: Optional[dict[str, Any]] = None
    ) -> dict[str, Any]:
        """Create a diff_patch node using tool_use_result for better diff generation.

        When Claude Code sessions include toolUseResult payloads (newer sessions),
        this method leverages the original file content to generate high-fidelity
        unified diffs that apply cleanly without manual fixes.

        Expected toolUseResult fields:
        - originalFile or originalFileContents: Complete original file content
        - oldString: The text being replaced (for validation)
        - newString: The replacement text

        Falls back to snippet-based diffs when full context is unavailable.
        """
        label = f"{tool_name} File {self.increment_counter()}"
        file_path = tool_input.get("file_path", "unknown")

        # Extract original file content from tool_use_result if available
        original_content = None
        if tool_use_result:
            original_content = tool_use_result.get("originalFile") or tool_use_result.get("originalFileContents")

            # For Edit operations, try using the direct diff generation
            if tool_name == "Edit":
                diff_content = self.diff_generator.generate_diff_from_tool_result(file_path, tool_use_result)
                if diff_content:
                    return {
                        "label": label,
                        "type": "diff_patch",
                        "position": self.get_position(),
                        "props": {
                            "target_path": file_path,
                            "diff": diff_content,
                            "format": "unified",
                            "backup": True,
                            "validate": True,
                        },
                    }

        # Fall back to standard edit node creation with original content if available
        return self.create_edit_node(tool_name, tool_input, original_content)

    def create_bash_node(self, tool_input: dict[str, Any]) -> dict[str, Any]:
        """Create a code_job node for bash command execution."""
        label = f"Bash Command {self.increment_counter()}"
        command = tool_input.get("command", "")
        description = tool_input.get("description", "Execute command")

        return {
            "label": label,
            "type": "code_job",
            "position": self.get_position(),
            "props": {
                "language": "bash",
                "code": command,
                "timeout": tool_input.get("timeout", 120000),
                "description": description,
            },
        }

    def create_todo_node(self, tool_input: dict[str, Any]) -> dict[str, Any]:
        """Create a DB node for TodoWrite operation."""
        label = f"Update TODO {self.increment_counter()}"
        todos = tool_input.get("todos", [])

        return {
            "label": label,
            "type": "db",
            "position": self.get_position(),
            "props": {
                "operation": "write",
                "sub_type": "memory",
                "query": "UPDATE TODO LIST",
                "data": {"todos": todos},
            },
        }

    def create_search_node(self, tool_name: str, tool_input: dict[str, Any]) -> dict[str, Any]:
        """Create a code_job node for search operations."""
        label = f"{tool_name} Search {self.increment_counter()}"

        if tool_name == "Grep":
            # Build ripgrep command from parameters
            pattern = tool_input.get("pattern", "")
            path = tool_input.get("path", ".")

            # Start with base command
            cmd_parts = ["rg"]

            # Add flags
            if tool_input.get("-n"):
                cmd_parts.append("-n")
            if tool_input.get("-i"):
                cmd_parts.append("-i")
            if tool_input.get("-A"):
                cmd_parts.append(f"-A {tool_input['-A']}")
            if tool_input.get("-B"):
                cmd_parts.append(f"-B {tool_input['-B']}")
            if tool_input.get("-C"):
                cmd_parts.append(f"-C {tool_input['-C']}")
            if tool_input.get("multiline"):
                cmd_parts.append("-U --multiline-dotall")

            # Add type filter if specified
            if tool_input.get("type"):
                cmd_parts.append(f"--type {tool_input['type']}")

            # Add glob filter if specified
            if tool_input.get("glob"):
                cmd_parts.append(f"--glob '{tool_input['glob']}'")

            # Add output mode handling
            output_mode = tool_input.get("output_mode", "files_with_matches")
            if output_mode == "files_with_matches":
                cmd_parts.append("-l")
            elif output_mode == "count":
                cmd_parts.append("-c")
            # "content" is default, no flag needed

            # Add pattern (properly escaped)
            escaped_pattern = pattern.replace("'", "'\\''")
            cmd_parts.append(f"'{escaped_pattern}'")

            # Add path
            cmd_parts.append(path)

            # Add head limit if specified
            if tool_input.get("head_limit"):
                cmd_parts.append(f"| head -n {tool_input['head_limit']}")

            code = " ".join(cmd_parts)

        elif tool_name == "Glob":
            # Build find command from glob pattern
            pattern = tool_input.get("pattern", "")
            path = tool_input.get("path", ".")

            # Convert glob pattern to find command
            # Common glob patterns: **/*.js, *.py, src/**/*.ts
            if "**" in pattern:
                # Recursive search
                name_pattern = pattern.replace("**/", "")
                code = f"find {path} -name '{name_pattern}' -type f"
            else:
                # Simple glob
                code = f"find {path} -maxdepth 1 -name '{pattern}' -type f"

            # Sort by modification time (newest first)
            code += " -printf '%T@ %p\\n' | sort -rn | cut -d' ' -f2-"

        else:
            # Fallback for unknown search tools
            query = tool_input.get("query", "") or tool_input.get("pattern", "")
            code = f"# {tool_name} search for: {query}"

        return {
            "label": label,
            "type": "code_job",
            "position": self.get_position(),
            "props": {
                "language": "bash",
                "code": code,
                "tool": tool_name,
                "description": f"{tool_name} search operation",
            },
        }

    def create_generic_tool_node(
        self, tool_name: str, tool_input: dict[str, Any]
    ) -> dict[str, Any]:
        """Create a generic API node for unknown tools."""
        label = f"{tool_name} {self.increment_counter()}"

        return {
            "label": label,
            "type": "api_job",
            "position": self.get_position(),
            "props": {
                "endpoint": f"/tools/{tool_name}",
                "method": "POST",
                "body": tool_input,
                "timeout": 30,
            },
        }

    def create_tool_node(
        self, tool_name: str, tool_input: dict[str, Any], tool_use_result: Optional[dict[str, Any]] = None
    ) -> Optional[dict[str, Any]]:
        """Create appropriate node based on tool name with defensive handling."""
        # Defensive handling for None or missing tool_name
        if not tool_name:
            print(f"Warning: Missing tool name, skipping node creation")
            return None

        # Track the tool being used
        self.text_processor.set_last_tool(tool_name)

        # Ensure tool_input is a dict
        if tool_input is None:
            tool_input = {}
        elif not isinstance(tool_input, dict):
            print(f"Warning: Invalid tool_input type for {tool_name}, using empty dict")
            tool_input = {}

        try:
            if tool_name == "Read":
                return self.create_read_node(tool_input)
            elif tool_name == "Write":
                return self.create_write_node(tool_input)
            elif tool_name in ["Edit", "MultiEdit"]:
                return self.create_edit_node_with_result(tool_name, tool_input, tool_use_result)
            elif tool_name == "Bash":
                return self.create_bash_node(tool_input)
            elif tool_name == "TodoWrite":
                return self.create_todo_node(tool_input)
            elif tool_name in ["Glob", "Grep"]:
                return self.create_search_node(tool_name, tool_input)
            else:
                # Generic API node for other tools
                return self.create_generic_tool_node(tool_name, tool_input)
        except Exception as e:
            print(f"Warning: Error creating {tool_name} node: {e}")
            # Fallback to generic node on error
            return self.create_generic_tool_node(tool_name, tool_input)

"""Text processing utilities for Claude Code translation."""

from typing import Any


class TextProcessor:
    """Handles text extraction and processing for Claude Code sessions."""

    def __init__(self):
        """Initialize text processor."""
        self.last_tool_name = None

    def set_last_tool(self, tool_name: str | None) -> None:
        """Set the last tool name for context-aware processing."""
        self.last_tool_name = tool_name

    def extract_text_content(self, content: Any, skip_read_results: bool = False) -> str:
        """Extract text content from various content formats.

        Args:
            content: The content to extract text from
            skip_read_results: If True, skip tool_result content from Read operations
        """
        if isinstance(content, str):
            return content

        if isinstance(content, list):
            text_parts = []
            for item in content:
                if isinstance(item, dict):
                    if item.get("type") == "text":
                        text_parts.append(item.get("text", ""))
                    elif item.get("type") == "tool_result":
                        # Skip tool results for execution/operation tools when processing user input
                        tools_to_skip = {
                            "Read",
                            "Bash",
                            "Write",
                            "Edit",
                            "MultiEdit",
                            "Glob",
                            "Grep",
                        }
                        if skip_read_results and self.last_tool_name in tools_to_skip:
                            pass  # Skip these tool results in user input nodes
                        else:
                            # Include other tool results (like WebFetch, Task, etc.) in the content
                            result_content = item.get("content", "")
                            if result_content:
                                text_parts.append(result_content)
                    elif "content" in item:
                        text_parts.append(
                            self.extract_text_content(item["content"], skip_read_results)
                        )
                elif isinstance(item, str):
                    text_parts.append(item)
            return " ".join(text_parts)

        if isinstance(content, dict):
            if "text" in content:
                return str(content["text"])
            if "content" in content:
                return self.extract_text_content(content["content"], skip_read_results)

        return str(content)

    @staticmethod
    def unescape_string(s: str) -> str:
        """Unescape a string that may have been escaped in JSON/JSONL format.

        Handles common escape sequences:
        - \\" becomes "
        - \\n becomes newline
        - \\t becomes tab
        - \\\\ becomes \\
        """
        if not s:
            return s

        # Use Python's built-in decode for escape sequences
        # This handles standard JSON escape sequences
        try:
            # First try to decode as if it were a JSON string value
            # We need to wrap it in quotes for json.loads to work
            import json

            decoded = json.loads('"' + s + '"')
            return decoded
        except:
            # If that fails, just do basic replacements
            s = s.replace('\\"', '"')
            s = s.replace("\\n", "\n")
            s = s.replace("\\t", "\t")
            s = s.replace("\\\\", "\\")
            return s

    """Parser and analyzer for Claude Code session JSONL files."""

    def __init__(self, session_id: str):
        """Initialize session with given ID."""
        self.session_id = session_id
        self.events: list[SessionEvent] = []
        self.metadata = SessionMetadata(session_id=session_id)

    def load_from_file(self, file_path: Path) -> None:
        """Load session events from a JSONL file."""
        if not file_path.exists():
            raise FileNotFoundError(f"Session file not found: {file_path}")

        self.events.clear()

        with open(file_path, encoding="utf-8") as f:
            for line_num, line in enumerate(f, 1):
                if not line.strip():
                    continue

                try:
                    data = json.loads(line)
                    event = SessionEvent.from_json(data)
                    self.events.append(event)
                except json.JSONDecodeError as e:
                    print(f"Warning: Failed to parse line {line_num}: {e}")
                except Exception as e:
                    print(f"Warning: Error processing line {line_num}: {e}")

        self._update_metadata()

    def parse_events(self) -> list[SessionEvent]:
        """Parse and return all session events."""
        return self.events

    def extract_tool_usage(self) -> dict[str, int]:
        """Extract and count tool usage from events."""
        tool_counts = defaultdict(int)

        for event in self.events:
            if event.tool_name:
                tool_counts[event.tool_name] += 1

        return dict(tool_counts)

    def get_conversation_flow(self) -> list[ConversationTurn]:
        """Extract conversation flow as user-assistant pairs with meta events."""
        turns = []
        current_turn = None
        pending_meta_events = []  # Collect meta events before each turn

        for event in self.events:
            # Collect meta/system events
            if event.is_meta:
                pending_meta_events.append(event)
                continue

            if event.type == "user":
                # Start a new turn
                if current_turn:
                    turns.append(current_turn)
                current_turn = ConversationTurn(user_event=event)

                # Attach any pending meta events to this turn
                if pending_meta_events:
                    current_turn.meta_events.extend(pending_meta_events)
                    pending_meta_events = []

            elif event.type == "assistant" and current_turn:
                # Add assistant response to current turn
                if not current_turn.assistant_event:
                    current_turn.assistant_event = event

                # Collect tool events
                if event.tool_name:
                    current_turn.tool_events.append(event)

        # Add the last turn if exists
        if current_turn:
            # Attach any remaining meta events
            if pending_meta_events:
                current_turn.meta_events.extend(pending_meta_events)
            turns.append(current_turn)

        return turns

    def get_file_operations(self) -> dict[str, list[str]]:
        """Extract all file operations (Read, Write, Edit) from the session."""
        operations = defaultdict(list)

        for event in self.events:
            if event.tool_name in ["Read", "Write", "Edit", "MultiEdit"]:
                if event.tool_input and "file_path" in event.tool_input:
                    file_path = event.tool_input["file_path"]
                    operations[event.tool_name].append(file_path)

        return dict(operations)

    def get_bash_commands(self) -> list[str]:
        """Extract all bash commands executed in the session."""
        commands = []

        for event in self.events:
            if event.tool_name == "Bash" and event.tool_input:
                if "command" in event.tool_input:
                    commands.append(event.tool_input["command"])

        return commands

    def _update_metadata(self) -> None:
        """Update session metadata based on parsed events."""
        if not self.events:
            return

        self.metadata.start_time = self.events[0].timestamp
        self.metadata.end_time = self.events[-1].timestamp
        self.metadata.event_count = len(self.events)
        self.metadata.tool_usage_count = self.extract_tool_usage()
        self.metadata.file_operations = self.get_file_operations()

    def get_summary_stats(self) -> dict[str, Any]:
        """Get summary statistics about the session."""
        stats = {
            "session_id": self.session_id,
            "total_events": len(self.events),
            "user_messages": sum(1 for e in self.events if e.type == "user"),
            "assistant_messages": sum(1 for e in self.events if e.type == "assistant"),
            "tool_usage": self.metadata.tool_usage_count,
            "unique_files_accessed": len(
                set(
                    file_path
                    for files in self.metadata.file_operations.values()
                    for file_path in files
                )
            ),
            "bash_commands_executed": len(self.get_bash_commands()),
        }

        if self.metadata.start_time and self.metadata.end_time:
            duration = self.metadata.end_time - self.metadata.start_time
            stats["duration_seconds"] = duration.total_seconds()
            stats["duration_human"] = str(duration)

        return stats


def find_session_files(base_dir: Path, limit: int = 50) -> list[Path]:
    """Find Claude Code session JSONL files in the given directory."""
    session_files = []

    if not base_dir.exists():
        return session_files

    # Look for JSONL files
    for jsonl_file in sorted(
        base_dir.glob("*.jsonl"), key=lambda p: p.stat().st_mtime, reverse=True
    ):
        session_files.append(jsonl_file)
        if len(session_files) >= limit:
            break

    return session_files


def parse_session_file(file_path: Path) -> ClaudeCodeSession:
    """Convenience function to parse a session file."""
    # Extract session ID from filename (assuming format: session-{id}.jsonl)
    session_id = file_path.stem.replace("session-", "")

    session = ClaudeCodeSession(session_id)
    session.load_from_file(file_path)

    return session

