def str_representer(dumper, data):
    # Check for both actual newlines and escaped newlines
    if "\n" in data or "\\n" in data:
        # Unescape the string if it contains escaped newlines
        if "\\n" in data and "\n" not in data:
            # Replace escaped newlines with actual newlines
            data = data.replace("\\n", "\n")
            # Also handle other common escapes
            data = data.replace("\\t", "\t")
            data = data.replace('\\"', '"')
        # Use literal style for multi-line strings (especially diffs)
        return dumper.represent_scalar("tag:yaml.org,2002:str", data, style="|")
    # Use default style for single-line strings
    return dumper.represent_scalar("tag:yaml.org,2002:str", data)

