            # Convert glob pattern to find command
            # Common glob patterns: **/*.js, *.py, src/**/*.ts
            if "**" in pattern:
                # Recursive search
                name_pattern = pattern.replace("**/", "")
                code = f"find {path} -name '{name_pattern}' -type f"
            else:
                # Simple glob
