  analyticsKey: raw.VITE_ANALYTICS_KEY ?? undefined,
  defaultLanguage: raw.VITE_DEFAULT_LOCALE ?? 'en',
  supportedLanguages: (raw.VITE_SUPPORTED_LOCALES ?? 'en').split(',').map(s => s.trim()),
  theme: {
    default: (raw.VITE_THEME_DEFAULT as any) ?? 'system',
    enableSystemToggle: (raw.VITE_ENABLE_THEME_TOGGLE ?? 'true') === 'true'
  },
  development_mode: {
    enable_mock_data: (raw.VITE_ENABLE_MOCK_DATA ?? 'true') === 'true',
    mock_auth_users: JSON.parse(raw.VITE_MOCK_AUTH_USERS ?? '[{"email":"nothing@void.com","password":"nothing123","role":"nothing_user"}]'),
    mock_api_endpoints: JSON.parse(raw.VITE_MOCK_API_ENDPOINTS ?? '["/api/nothing","/api/testimonials/nothing","/api/pricing/nothing","/api/support/nothing","/api/newsletter/nothing","/api/analytics/nothing","/api/checkout/nothing"]'),
    disable_websocket_in_dev: (raw.VITE_DISABLE_WEBSOCKET_IN_DEV ?? 'false') === 'true',
    use_localstorage_persistence: (raw.VITE_USE_LOCALSTORAGE_PERSISTENCE ?? 'true') === 'true'
  }
};

// React Query default options exposed here so QueryProvider can import them
export const defaultQueryOptions: DefaultOptions = {
  queries: {
    retry: (failureCount, error) => {
      // fail fast for 4xx client errors
      const status = (error as any)?.response?.status;
      if (status && status >= 400 && status < 500) return false;
      return failureCount < 2;
    },
    cacheTime: 1000 * 60 * 5, // 5 minutes
    staleTime: 1000 * 60, // 1 minute
    refetchOnWindowFocus: false,
    suspense: true
  },
  mutations: {
    retry: false
  }
};

// i18n configuration object (consumed by i18next initialization elsewhere)
export const i18nConfig = {
  lng: appConfig.defaultLanguage,
  fallbackLng: 'en',
  supportedLngs: appConfig.supportedLanguages,
  ns: ['common', 'hero', 'pricing', 'testimonials'] as const,
  defaultNS: 'common'
};

// Theme configuration (consumed by ThemeProvider)
export const themeConfig = {
  default: appConfig.theme.default,
  enableSystemToggle: appConfig.theme.enableSystemToggle,
  // Tailwind dark-mode strategy usedin app: 'class'
  tailwindDarkClass: 'dark'
};

