export const AuthUserSchema = z.object({
  id: z.string(),
  email: z.string().email(),
  role: z.string(),
  displayName: z.string().optional(),
  createdAt: z.string().optional()
});
export type AuthUser = z.infer<typeof AuthUserSchema>;

