import { appConfig } from '@/app/config';

type ServiceFactory<T = any> = () => T;
type ServiceInstance<T = any> = T;

interface ServiceEntry<T = any> {
  factory?: ServiceFactory<T>;
  instance?: ServiceInstance<T>;
  singleton?: boolean;
}

class DIContainer {
  private services = new Map<string, ServiceEntry>();
  private resolving = new Set<string>();

  /**
   * Register a service factory or instance
   */
  register<T>(
    name: string,
    factoryOrInstance: ServiceFactory<T> | T,
    options: { singleton?: boolean } = {}
  ): void {
    const { singleton = true } = options;

    if (typeof factoryOrInstance === 'function') {
      this.services.set(name, {
        factory: factoryOrInstance as ServiceFactory<T>,
        singleton
      });
    } else {
      this.services.set(name, {
        instance: factoryOrInstance,
        singleton: true
      });
    }
  }

  /**
   * Resolve a service by name
   */
  resolve<T>(name: string): T {
    if (this.resolving.has(name)) {
      throw new Error(`Circular dependency detected: ${name}`);
    }

    const entry = this.services.get(name);
    if (!entry) {
      throw new Error(`Service not found: ${name}`);
    }

    // Return existing instance if singleton and already created
    if (entry.singleton && entry.instance !== undefined) {
      return entry.instance as T;
    }

    // Create new instance
    if (entry.factory) {
      this.resolving.add(name);

      try {
        const instance = entry.factory();

        if (entry.singleton) {
          entry.instance = instance;
        }

        this.resolving.delete(name);
        return instance as T;
      } catch (error) {
        this.resolving.delete(name);
        throw error;
      }
    }

    // Return non-singleton instance
    if (entry.instance !== undefined) {
      return entry.instance as T;
    }

    throw new Error(`Unable to resolve service: ${name}`);
  }

  /**
   * Check if a service is registered
   */
  has(name: string): boolean {
    return this.services.has(name);
  }

  /**
   * Override a service (useful for testing)
   */
  override<T>(name: string, factoryOrInstance: ServiceFactory<T> | T): void {
    this.register(name, factoryOrInstance, { singleton: true });
  }

  /**
   * Clear all services
   */
  clear(): void {
    this.services.clear();
    this.resolving.clear();
  }

  /**
   * Get all registered service names
   */
  getServiceNames(): string[] {
    return Array.from(this.services.keys());
  }
}

// Global container instance
export const container = new DIContainer();

// Convenience functions
export function registerService<T>(
  name: string,
  factoryOrInstance: ServiceFactory<T> | T,
  options?: { singleton?: boolean }
): void {
  container.register(name, factoryOrInstance, options);
}

export function resolveService<T>(name: string): T {
  return container.resolve<T>(name);
}

export function overrideService<T>(name: string, factoryOrInstance: ServiceFactory<T> | T): void {
  container.override(name, factoryOrInstance);
}

export function hasService(name: string): boolean {
  return container.has(name);
}

// Service registration helper for lazy initialization
export function lazy<T>(factory: () => T): ServiceFactory<T> {
  return factory;
}

// Development helpers
if (appConfig.env === 'development') {
  // Expose container on window for debugging
  (globalThis as any).__DI_CONTAINER__ = container;
}

// Common service names (constants to avoid typos)
export const SERVICE_NAMES = {
  ANALYTICS: 'analytics',
  PRICING: 'pricing',
  CHAT: 'chat',
  TESTIMONIALS: 'testimonials',
  NEWSLETTER: 'newsletter',
