// src/core/events.ts

/* src/core/events.ts

Lightweight event bus for cross-feature communication (e.g., 'purchase:nothing', 'analytics:event', 'easter:found'). Supports typed payloads from core/contracts and allows plugin subscriptions.
*/

// Self-Confirm Comments:
// [x] Uses `@/` imports as much as possible
// [x] Uses providers/hooks (no direct DOM/localStorage side effects)
// [x] Reads config from `@/app/config`
// [x] Exports default named component
// [x] Adds basic ARIA and keyboard handlers (where relevant)

import { AnalyticsEvent } from '@/core/contracts';

type EventHandler<T = any> = (payload: T) => void | Promise<void>;
type EventMap = Record<string, any>;

interface EventSubscription {
  id: string;
  handler: EventHandler;
  once?: boolean;
}

class EventBusClass {
  private subscriptions = new Map<string, EventSubscription[]>();
  private subscriptionCounter = 0;

  /**
   * Emit an event with optional payload
   */
  emit<TEventMap extends EventMap, TKey extends keyof TEventMap>(
    eventName: TKey,
    payload?: TEventMap[TKey]
  ): void {
    const eventNameStr = String(eventName);
    const handlers = this.subscriptions.get(eventNameStr) || [];

    // Execute handlers
    handlers.forEach(subscription => {
      try {
        subscription.handler(payload);
      } catch (error) {
        console.error(`Event handler error for ${eventNameStr}:`, error);
      }
    });

    // Remove one-time handlers
    const remainingHandlers = handlers.filter(sub => !sub.once);
    if (remainingHandlers.length !== handlers.length) {
      this.subscriptions.set(eventNameStr, remainingHandlers);
    }
  }

  /**
   * Subscribe to an event
   */
  on<TEventMap extends EventMap, TKey extends keyof TEventMap>(
    eventName: TKey,
    handler: EventHandler<TEventMap[TKey]>,
    options: { once?: boolean } = {}
  ): () => void {
    const eventNameStr = String(eventName);
    const subscriptionId = `sub_${++this.subscriptionCounter}`;

    const subscription: EventSubscription = {
      id: subscriptionId,
      handler,
      once: options.once
    };

    if (!this.subscriptions.has(eventNameStr)) {
      this.subscriptions.set(eventNameStr, []);
    }

    this.subscriptions.get(eventNameStr)!.push(subscription);

    // Return unsubscribe function
    return () => this.off(eventName, subscriptionId);
  }

  /**
   * Subscribe to an event only once
   */
  once<TEventMap extends EventMap, TKey extends keyof TEventMap>(
    eventName: TKey,
    handler: EventHandler<TEventMap[TKey]>
  ): () => void {
    return this.on(eventName, handler, { once: true });
  }

  /**
   * Unsubscribe from an event
   */
  off<TEventMap extends EventMap, TKey extends keyof TEventMap>(
    eventName: TKey,
    subscriptionIdOrHandler?: string | EventHandler<TEventMap[TKey]>
  ): void {
    const eventNameStr = String(eventName);
    const handlers = this.subscriptions.get(eventNameStr);

    if (!handlers) return;

    if (!subscriptionIdOrHandler) {
      // Remove all handlers for this event
      this.subscriptions.delete(eventNameStr);
      return;
    }

    if (typeof subscriptionIdOrHandler === 'string') {
      // Remove by subscription ID
      const filteredHandlers = handlers.filter(sub => sub.id !== subscriptionIdOrHandler);
      this.subscriptions.set(eventNameStr, filteredHandlers);
    } else {
      // Remove by handler function
      const filteredHandlers = handlers.filter(sub => sub.handler !== subscriptionIdOrHandler);
      this.subscriptions.set(eventNameStr, filteredHandlers);
    }
  }

  /**
   * Get all event names that have subscriptions
   */
  getEventNames(): string[] {
    return Array.from(this.subscriptions.keys());
  }

  /**
   * Get subscription count for an event
   */
  getSubscriptionCount(eventName: string): number {
    return this.subscriptions.get(eventName)?.length || 0;
  }

  /**
   * Clear all subscriptions
   */
  clear(): void {
    this.subscriptions.clear();
  }
}

// Global event bus instance
export const EventBus = new EventBusClass();

// Convenience functions
export function emitEvent<TEventMap extends EventMap, TKey extends keyof TEventMap>(
  eventName: TKey,
  payload?: TEventMap[TKey]
): void {
  EventBus.emit(eventName, payload);
}

export function onEvent<TEventMap extends EventMap, TKey extends keyof TEventMap>(
  eventName: TKey,
  handler: EventHandler<TEventMap[TKey]>,
  options?: { once?: boolean }
): () => void {
  return EventBus.on(eventName, handler, options);
}

export function offEvent<TEventMap extends EventMap, TKey extends keyof TEventMap>(
  eventName: TKey,
  subscriptionIdOrHandler?: string | EventHandler<TEventMap[TKey]>
): void {
  EventBus.off(eventName, subscriptionIdOrHandler);
}

export function onceEvent<TEventMap extends EventMap, TKey extends keyof TEventMap>(
  eventName: TKey,
  handler: EventHandler<TEventMap[TKey]>
): () => void {
  return EventBus.once(eventName, handler);
}

// Typed event definitions for the Nothing app
export interface NothingEventMap {
  // Analytics events
  'analytics:track': AnalyticsEvent;
  'analytics:page_view': { page: string; timestamp: string };

  // Purchase events
  'purchase:nothing': { tier: string; amount: number };
  'purchase:attempt': { tier: string };
  'purchase:cancelled': { tier: string; reason?: string };

  // User interaction events
  'user:newsletter_signup': { email: string };
  'user:chat_message': { message: string; timestamp: string };
  'user:void_simulation': { action: 'start' | 'stop' | 'reset' };

  // Easter egg events
  'easter:found': { type: string; location: string };
  'easter:sequence_complete': { sequence: string };

  // UI events
  'ui:theme_changed': { theme: 'light' | 'dark' | 'system' };
  'ui:modal_opened': { modal: string };
  'ui:modal_closed': { modal: string };

  // Error events
  'error:api_failed': { endpoint: string; error: string };
  'error:form_validation': { form: string; errors: Record<string, string> };

  // Feature events
  'feature:testimonial_generated': { testimonial: any };
  'feature:pricing_tier_selected': { tier: string };
  'feature:faq_searched': { query: string; results: number };
}

// Typed convenience functions for the Nothing app
export function emitNothingEvent<TKey extends keyof NothingEventMap>(
  eventName: TKey,
  payload: NothingEventMap[TKey]
): void {
  EventBus.emit(eventName, payload);
}

export function onNothingEvent<TKey extends keyof NothingEventMap>(
  eventName: TKey,
  handler: EventHandler<NothingEventMap[TKey]>,
  options?: { once?: boolean }
): () => void {
  return EventBus.on(eventName, handler, options);
}

// Common event names constants
export const EVENT_NAMES = {
  ANALYTICS_TRACK: 'analytics:track' as const,
  PURCHASE_NOTHING: 'purchase:nothing' as const,
  USER_NEWSLETTER: 'user:newsletter_signup' as const,
  EASTER_FOUND: 'easter:found' as const,
  UI_THEME_CHANGED: 'ui:theme_changed' as const,
  ERROR_API_FAILED: 'error:api_failed' as const,
  FEATURE_PRICING_SELECTED: 'feature:pricing_tier_selected' as const
} as const;

// Example usage:
// emitNothingEvent(EVENT_NAMES.PURCHASE_NOTHING, { tier: 'pro', amount: 999 });
// onNothingEvent(EVENT_NAMES.EASTER_FOUND, (payload) => console.log('Easter egg found!', payload));
```
